import React from 'react'
import './style.css'

function Box(props){
        
    function pickMarble(){
        //console.log(props.row + ' ' + props.col)
        if(props.currentVal === '0')        {
            setText(props.currentVal)
            props.move(props.row, props.col, 'Pick')
        }
    }

    function releaseMarble(){
        //console.log(props.row + ' ' + props.col)
        if(props.currentVal != '0')        {
            setText(props.currentVal)
            props.move(props.row, props.col, 'Release')
        }
    }

    return <div className='box' onMouseDown={pickMarble} onMouseUp={releaseMarble} >{ (props.row == 3 && props.col == 4) ? '' : props.currentVal }</div>
}

export default Box
